mapper.select:
select table_name tableName, column_name columnName,COLUMN_COMMENT columnComment
from information_schema.COLUMNS
WHERE table_schema = (SELECT DATABASE()) and table_name =#{tableName}

SELECT C.relname tableName, A.attname AS columnName, col_description (A.attrelid,A.attnum) AS columnComment
FROM pg_class AS C inner join pg_attribute AS A on A.attrelid = C.oid
WHERE A.attnum> 0 AND NOT A.attisdropped and C.relname =#{tableName}
